import java.util.Scanner;public class LettersOfString {    public static int countLetters(String inputString) {        int count = inputString.length();        return count;    }    public static void main(String[] args) {        System.out.println("Input a string: ");        Scanner scanner = new Scanner(System.in);        String string = scanner.next();        System.out.println("This string has " + countLetters(string) + " letters.");    }}